const categories = ["All", "Frontend", "Backend", "Database", "Language", "DevOps", "Cloud"];

export function Technologies() {
  const [selectedCategory, setSelectedCategory] = useState("All");

  const filteredTechnologies =
    selectedCategory === "All"
      ? technologies
      : technologies.filter((tech) => tech.category === selectedCategory);

  return (
    <section
      id="technologies"
      className="relative z-10 min-h-screen sm:px-6 lg:px-8"
    >
      <div className="mx-auto max-w-7xl">
        <div className="mb-16 text-center">
          <h2 className="mb-4 text-4xl font-bold tracking-tight text-black dark:text-white sm:text-5xl md:text-6xl [font-family:var(--font-space-grotesk)]">
            Technologies We Use
          </h2>
          <p className="mx-auto max-w-2xl text-lg text-zinc-600 dark:text-zinc-400">
            Modern tech stack for building scalable and performant applications
          </p>
        </div>

        <div className="mb-12 flex flex-wrap justify-center gap-3">
          {categories.map((category) => (
            <button
              key={category}
              onClick={() => setSelectedCategory(category)}
              className={`rounded-full px-4 py-2 text-sm font-medium transition-all ${
                selectedCategory === category
                  ? "bg-black text-white dark:bg-white dark:text-black"
                  : "bg-white text-zinc-700 hover:bg-zinc-100 dark:bg-zinc-900 dark:text-zinc-300 dark:hover:bg-zinc-800"
              }`}
            >
              {category}
            </button>
          ))}
        </div>

        <div className="grid grid-cols-2 gap-4 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6">
          {filteredTechnologies.map((tech) => (
            <Card
              key={tech.name}
              className="group cursor-pointer border-zinc-200 bg-white transition-all hover:scale-105 hover:shadow-lg dark:border-zinc-800 dark:bg-zinc-900"
            >
              <CardContent className="flex flex-col items-center justify-center p-6">
                <div className="mb-3 flex h-12 w-12 items-center justify-center rounded-full bg-gradient-to-br from-zinc-200 to-zinc-300 dark:from-zinc-700 dark:to-zinc-800">
                  <div
                    className="h-6 w-6"
                    dangerouslySetInnerHTML={{ __html: tech.svg }}
                  />
                </div>
                <p className="text-center text-sm font-medium text-zinc-900 dark:text-zinc-100">
                  {tech.name}
                </p>
                <Badge
                  variant="secondary"
                  className="mt-2 text-xs text-zinc-600 dark:text-zinc-400"
                >
                  {tech.category}
                </Badge>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    </section>
  );
}
